	def who_win(self, board, char):
		possible = [[0, 1, 2], [3, 4, 5], [6, 7, 8], [0, 3, 6], [1, 4, 7], [2, 5, 8], [0, 4, 8], [2, 4, 6]]
		for i in range(8):
			if board[possible[i][0]] == char and board[possible[i][1]] == char and (board[possible[i][2]] == char):
				return True
		return False

	def isValid(self, board):
		countX = board.count('X')
		countO = board.count('O')
		if countX == countO or countX == countO + 1:
			if self.who_win(board, 'O'):
				if self.who_win(board, 'X'):
					return False
				if countX == countO:
					return True
			if self.who_win(board, 'X') and countX != countO + 1:
				return False
			if not self.who_win(board, 'O'):
				return True
		return False
