", "class Solution:

	def winner(self, board, val):
		cd1 = 0
		cd2 = 0
		for i in range(3):
			cr = 0
			cc = 0
			for j in range(3):
				if board[i][j] == val:
					cr += 1
				if board[j][i] == val:
					cc += 1
				if i == j and board[i][j] == val:
					cd1 += 1
				if i + j == 2 and board[i][j] == val:
					cd2 += 1
			if cr == 3 or cc == 3 or cd1 == 3 or (cd2 == 3):
				return True
		return False

	def isValid(self, x):
		cx = 0
		co = 0
		board = []
		tot = []
		for i in range(9):
			if i > 0 and i % 3 == 0:
				board.append(tot)
				tot = []
			tot.append(x[i])
		board.append(tot)
		for i in range(3):
			for j in range(3):
				if board[i][j] == 'X':
					cx += 1
				elif board[i][j] == 'O':
					co += 1
		xwin = self.winner(board, 'X')
		owin = self.winner(board, 'O')
		if cx - co > 1 or co > cx:
			return False
		if owin:
			if not xwin and cx - co == 0:
				return True
			else:
				return False
		if xwin:
			if cx - co == 1:
				return True
			return False
		return True
