			if gcdn == gcdns[-1][0]:
				gcdns[-1][1] += c
			else:
				gcdns.append([gcdn, c])
		visited[u] = True
		i = bisect_left(edges, [u, 0])
		if i == 2 * (n - 1):
			continue
		(w, v) = edges[i]
		while w == u and i < 2 * (n - 1):
			if not visited[v]:
				dq.append((v, gcdns))
			i += 1
			if i < 2 * (n - 1):
				(w, v) = edges[i]
	print(sum)
main()
