from sys import stdin

def next_int(gen):
	return int(next(gen))

def next_float(gen):
	return float(next(gen))

def intar(size):
	return array('i', 0) * size

def next_arr(gen, size=None, type='i'):
	if size == None:
		size = next_int(gen)
	arr = array(type, [0]) * size
	for x in range(size):
		arr[x] = next_int(gen)
	return arr

def scan_gen():
	for line in stdin:
		yield from iter(line.split())

def foo(s):
	if len(s) > 10:
		return f'{s[0]}{len(s) - 2}{s[-1]}'
	return s

def solve():
	scan = scan_gen()
	n = next_int(scan)
	w = [next(scan) for _ in range(n)]
	res = '\n'.join(map(foo, w))
	print(res)
