	first = word[0]
	last = word[-1]
	return first + str(length) + last

def solve(n, words):
	abbreviated_words = []
	for word in words:
		if len(word) <= 10:
			abbreviated_words.append(word)
		else:
			abbreviated_word = abbreviate_word(word)
			abbreviated_words.append(abbreviated_word)
	return abbreviated_words
