", "import sys

def calc_possibility(open_cards):
	cards = [x for x in range(1, 11)]
	(c1, c2, c3) = open_cards
	my_total = c1 + c2
	cards.remove(c1)
	cards.remove(c2)
	cards.remove(c3)
	combinations = 0
	hit = 0
	for c in cards:
		combinations += 1
		if my_total + c <= 20:
			hit += 1
	return hit / combinations >= 0.5

def main(args):
	for line in sys.stdin:
		open_cards = [int(x) for x in line.strip().split(' ')]
		if calc_possibility(open_cards):
			print('YES')
		else:
			print('NO')
main(sys.argv[1:])
